const valid_data = data.series.length > 2 && data.series[0].fields.length > 0
if (!valid_data) {
  return {
    "layout": {
        "xaxis": {
            "visible": false
        },
        "yaxis": {
            "visible": false
        },
        "annotations": [
            {
                "text": "No Data",
                "xref": "paper",
                "yref": "paper",
                "showarrow": false,
                "font": {
                    "size": 28
                }
            }
        ]
    }
  }
}

const colors = ["rgba(255, 0, 0,0.7)", "rgba(0, 255, 0,0.7)", "rgba(0, 0, 255,0.7)"]
const traces = ["x","y","z"].map((axis, i) => {
  let x = data.series[i].fields[0].values.buffer;
  let y = data.series[i].fields[1].values.buffer;
  // t = [...Array(t.length).keys()].map(v => (v/t.length)* 365)
  return {
    x: x,
    y: y,
    name: axis,
    type: "scatter",
    mode: "lines",
    line: {color: colors[i]}
  }
})


layout = {polar: {radialaxis: { range: [-0.05, 0.03]}}}

return { data: traces, layout: layout };
